import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CO2_API_KEY = 'Bc0tU7kRVtzaHa6FQLcw'; // Replace with your Carbon Interface API key

export const fetchVehicleMakes = async () => {
  try {
    const response = await axios.get(`https://www.carboninterface.com/api/v1/vehicle_makes`, {
      headers: {
        Authorization: `Bearer ${CO2_API_KEY}`,
      },
    });
    return response.data.map((make: any) => ({
      id: make.data.id,
      name: make.data.attributes.name,
    }));
  } catch (error: any) {
    console.error('Error fetching vehicle makes:', error.response?.data || error.message);
    return [];
  }
};

export const fetchVehicleModels = async (makeId: string) => {
  try {
    const response = await axios.get(
      `https://www.carboninterface.com/api/v1/vehicle_makes/${makeId}/vehicle_models`,
      {
        headers: {
          Authorization: `Bearer ${CO2_API_KEY}`,
        },
      }
    );
    return response.data.map((model: any) => ({
      id: model.data.id,
      name: `${model.data.attributes.name} (${model.data.attributes.year})`,
    }));
  } catch (error: any) {
    console.error('Error fetching vehicle models:', error.response?.data || error.message);
    return [];
  }
};

export const calculateCO2 = async (distance: number, vehicleModelId: string) => {
  try {
    const response = await axios.post(
      `https://www.carboninterface.com/api/v1/estimates`,
      {
        type: 'vehicle',
        distance_unit: 'km',
        distance_value: distance,
        vehicle_model_id: vehicleModelId,
      },
      {
        headers: {
          Authorization: `Bearer ${CO2_API_KEY}`,
          'Content-Type': 'application/json',
        },
      }
    );
    return response.data;
  } catch (error: any) {
    console.error('Error calculating CO2 emissions:', error.response?.data || error.message);
    return null;
  }
};

const CO2Calculator: React.FC = () => {
  const [distance, setDistance] = useState('');
  const [vehicleMakes, setVehicleMakes] = useState<any[]>([]);
  const [selectedMake, setSelectedMake] = useState('');
  const [vehicleModels, setVehicleModels] = useState<any[]>([]);
  const [selectedModel, setSelectedModel] = useState('');
  const [result, setResult] = useState<any | null>(null);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchMakes = async () => {
      const makes = await fetchVehicleMakes();
      setVehicleMakes(makes);
    };
    fetchMakes();
  }, []);

  useEffect(() => {
    if (selectedMake) {
      const fetchModels = async () => {
        const models = await fetchVehicleModels(selectedMake);
        setVehicleModels(models);
      };
      fetchModels();
    } else {
      setVehicleModels([]);
    }
  }, [selectedMake]);

  const handleCalculate = async () => {
    if (!distance.trim() || !selectedModel.trim()) {
      setError('Please enter both distance and select a vehicle model.');
      return;
    }
    setError(null); // Clear any previous errors
    const co2Result = await calculateCO2(Number(distance), selectedModel);
    if (co2Result) {
      setResult(co2Result);
    } else {
      setError('An error occurred while calculating CO2 emissions.');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-full bg-gray-100 text-center p-4">
      <h1 className="text-3xl font-bold text-blue-600 mb-4">CO2 Calculator</h1>
      <input
        type="text"
        value={distance}
        onChange={(e) => setDistance(e.target.value)}
        placeholder="Enter distance in km..."
        className="border border-gray-300 rounded p-2 mb-4"
      />
      <select
        value={selectedMake}
        onChange={(e) => setSelectedMake(e.target.value)}
        className="border border-gray-300 rounded p-2 mb-4"
      >
        <option value="">Select vehicle make...</option>
        {vehicleMakes.map((make) => (
          <option key={make.id} value={make.id}>
            {make.name}
          </option>
        ))}
      </select>
      <select
        value={selectedModel}
        onChange={(e) => setSelectedModel(e.target.value)}
        className="border border-gray-300 rounded p-2 mb-4"
        disabled={!selectedMake}
      >
        <option value="">Select vehicle model...</option>
        {vehicleModels.map((model) => (
          <option key={model.id} value={model.id}>
            {model.name}
          </option>
        ))}
      </select>
      <button
        onClick={handleCalculate}
        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
      >
        Calculate
      </button>
      {error && <p className="text-red-500 mt-2">{error}</p>}
      {result && (
        <div className="mt-4 p-4 bg-white rounded shadow w-full max-w-md">
          <h2 className="text-2xl font-bold text-gray-800">CO2 Emissions</h2>
          <p className="text-lg text-gray-700">
            Distance: {result.data.attributes.distance_value} km
          </p>
          <p className="text-lg text-gray-700">
            Vehicle: {result.data.attributes.vehicle_make} {result.data.attributes.vehicle_model} ({result.data.attributes.vehicle_year})
          </p>
          <p className="text-lg text-gray-700">
            CO2 Emissions: {result.data.attributes.carbon_kg} kg
          </p>
        </div>
      )}
    </div>
  );
};

export default CO2Calculator;
