import React, { useState, useEffect } from 'react';
import axios from 'axios';

const WEATHER_API_KEY = '418fc9c9afe10063f84ae33a70965f05'; // Replace with your OpenWeatherMap API key
const GOOGLE_MAPS_API_KEY = 'your_google_maps_api_key'; // Replace with your Google Maps API key

export const fetchWeather = async (city: string) => {
  try {
    const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather`, {
      params: {
        q: city,
        appid: WEATHER_API_KEY,
        units: 'metric',
      },
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching weather data:', error);
    return null;
  }
};

const Weather: React.FC = () => {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState<any | null>(null);
  const [error, setError] = useState<string | null>(null);

  const handleSearch = async () => {
    if (!city.trim()) {
      setError('Please enter a city name.');
      return;
    }
    setError(null); // Clear any previous errors
    const result = await fetchWeather(city);
    if (result) {
      setWeather(result);
    } else {
      setError('City not found or an error occurred.');
    }
  };

  useEffect(() => {
    if (weather) {
      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=${AIzaSyDEd3FT5kbshAHU0-v1jOKCh2E_zyJiSCs}`;
      script.async = true;
      script.onload = () => {
        const map = new google.maps.Map(document.getElementById('map') as HTMLElement, {
          center: { lat: weather.coord.lat, lng: weather.coord.lon },
          zoom: 10,
        });

        new google.maps.Marker({
          position: { lat: weather.coord.lat, lng: weather.coord.lon },
          map,
          title: weather.name,
        });
      };
      document.body.appendChild(script);
    }
  }, [weather]);

  return (
    <div className="flex flex-col items-center justify-center h-full bg-gray-100 text-center p-4">
      <h1 className="text-3xl font-bold text-blue-600 mb-4">Weather Search</h1>
      <input
        type="text"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        placeholder="Enter city name..."
        className="border border-gray-300 rounded p-2 mb-4"
      />
      <button
        onClick={handleSearch}
        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
      >
        Search
      </button>
      {error && <p className="text-red-500 mt-2">{error}</p>}
      {weather && (
        <div className="mt-4 p-4 bg-white rounded shadow w-full max-w-md">
          <h2 className="text-2xl font-bold text-gray-800">{weather.name}</h2>
          <p className="text-lg text-gray-700">Temperature: {weather.main.temp}Â°C</p>
          <p className="text-lg text-gray-700">Weather: {weather.weather[0].description}</p>
          <p className="text-lg text-gray-700">Humidity: {weather.main.humidity}%</p>
          <p className="text-lg text-gray-700">Wind Speed: {weather.wind.speed} m/s</p>
          <div id="map" className="mt-4" style={{ height: '300px', width: '100%' }}></div>
        </div>
      )}
    </div>
  );
};

export default Weather;
