import React, { useState } from 'react';
import axios from 'axios';

const CLIMATIQ_API_KEY = '4T8QZ62NW10R17EYJ6RX9GR248'; // Replace with your Climatiq API key

// Define the type for the API response
interface CO2Response {
  co2e: number;
  co2e_unit: string;
  activity_data: {
    distance: number;
    distance_unit: string;
    vehicle_type: string;
  };
}

// Define the calculateCO2 function with proper types
export const calculateCO2 = async (
  distance: number,
  vehicleType: string
): Promise<CO2Response | null> => {
  try {
    const response = await axios.post<CO2Response>(
      `https://beta3.api.climatiq.io/estimate`,
      {
        emission_factor: {
          activity_id: 'passenger_vehicle-vehicle_type_automobile-fuel_source_na-distance_na-engine_size_na',
        },
        parameters: {
          distance: distance,
          distance_unit: 'km',
          vehicle_type: vehicleType,
        },
      },
      {
        headers: {
          Authorization: `Bearer ${CLIMATIQ_API_KEY}`,
          'Content-Type': 'application/json',
        },
      }
    );
    return response.data;
  } catch (error: any) {
    console.error('Error calculating CO2 emissions:', error.response?.data || error.message);
    return null;
  }
};

const CO2Calculator: React.FC = () => {
  const [distance, setDistance] = useState('');
  const [vehicleType, setVehicleType] = useState('');
  const [result, setResult] = useState<CO2Response | null>(null);
  const [error, setError] = useState<string | null>(null);

  const handleCalculate = async () => {
    if (!distance.trim() || !vehicleType.trim()) {
      setError('Please enter both distance and vehicle type.');
      return;
    }
    setError(null);
    const co2Result = await calculateCO2(Number(distance), vehicleType);
    if (co2Result) {
      setResult(co2Result);
    } else {
      setError('An error occurred while calculating CO2 emissions.');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-full bg-gray-100 text-center p-4">
      <h1 className="text-3xl font-bold text-blue-600 mb-4">CO2 Calculator</h1>
      <input
        type="text"
        value={distance}
        onChange={(e) => setDistance(e.target.value)}
        placeholder="Enter distance in km..."
        className="border border-gray-300 rounded p-2 mb-4"
      />
      <select
        value={vehicleType}
        onChange={(e) => setVehicleType(e.target.value)}
        className="border border-gray-300 rounded p-2 mb-4"
      >
        <option value="">Select vehicle type...</option>
        <option value="small_petrol_car">Small Petrol Car</option>
        <option value="medium_petrol_car">Medium Petrol Car</option>
        <option value="large_petrol_car">Large Petrol Car</option>
        <option value="small_diesel_car">Small Diesel Car</option>
        <option value="medium_diesel_car">Medium Diesel Car</option>
        <option value="large_diesel_car">Large Diesel Car</option>
        <option value="electric_car">Electric Car</option>
        <option value="hybrid_car">Hybrid Car</option>
      </select>
      <button
        onClick={handleCalculate}
        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
      >
        Calculate
      </button>
      {error && <p className="text-red-500 mt-2">{error}</p>}
      {result && (
        <div className="mt-4 p-4 bg-white rounded shadow w-full max-w-md">
          <h2 className="text-2xl font-bold text-gray-800">CO2 Emissions</h2>
          <p className="text-lg text-gray-700">Distance: {result.activity_data.distance} km</p>
          <p className="text-lg text-gray-700">Vehicle Type: {result.activity_data.vehicle_type}</p>
          <p className="text-lg text-gray-700">
            CO2 Emissions: {result.co2e} {result.co2e_unit}
          </p>
        </div>
      )}
    </div>
  );
};

export default CO2Calculator;
