import React, { useState } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

const WEATHER_API_KEY = '418fc9c9afe10063f84ae33a70965f05'; // Replace with your OpenWeatherMap API key

export const fetchWeather = async (city: string) => {
  try {
    const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather`, {
      params: {
        q: city,
        appid: WEATHER_API_KEY,
        units: 'metric',
      },
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching weather data:', error);
    return null;
  }
};

const Weather: React.FC = () => {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState<any | null>(null);
  const [error, setError] = useState<string | null>(null);

  const handleSearch = async () => {
    if (!city.trim()) {
      setError('Please enter a city name.');
      return;
    }
    setError(null); // Clear any previous errors
    const result = await fetchWeather(city);
    if (result) {
      setWeather(result);
    } else {
      setError('City not found or an error occurred.');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-full bg-gray-100 text-center p-4">
      <h1 className="text-3xl font-bold text-blue-600 mb-4">Weather Search</h1>
      <input
        type="text"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        placeholder="Enter city name..."
        className="border border-gray-300 rounded p-2 mb-4"
      />
      <button
        onClick={handleSearch}
        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
      >
        Search
      </button>
      {error && <p className="text-red-500 mt-2">{error}</p>}
      {weather && (
        <div className="mt-4 p-4 bg-white rounded shadow w-full max-w-md">
          <h2 className="text-2xl font-bold text-gray-800">{weather.name}</h2>
          <p className="text-lg text-gray-700">Temperature: {weather.main.temp}°C</p>
          <p className="text-lg text-gray-700">Weather: {weather.weather[0].description}</p>
          <p className="text-lg text-gray-700">Humidity: {weather.main.humidity}%</p>
          <p className="text-lg text-gray-700">Wind Speed: {weather.wind.speed} m/s</p>
          <p className="text-lg text-gray-700">Wind Direction: {weather.wind.deg}°</p>
          <div className="mt-4">
            <MapContainer
              center={[weather.coord.lat, weather.coord.lon]}
              zoom={10}
              style={{ height: '300px', width: '100%' }}
            >
              <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
              />
              <Marker position={[weather.coord.lat, weather.coord.lon]}>
                <Popup>
                  <div>
                    <p><strong>{weather.name}</strong></p>
                    <p>Wind Speed: {weather.wind.speed} m/s</p>
                    <p>Wind Direction: {weather.wind.deg}°</p>
                  </div>
                </Popup>
              </Marker>
            </MapContainer>
          </div>
        </div>
      )}
    </div>
  );
};

export default Weather;
