import React, { useState } from 'react';
import axios from 'axios';

const CLIMATIQ_API_KEY = '4T8QZ62NW10R17EYJ6RX9GR248'; // Replace with your Climatiq API key

// Define the type for the API response
interface CO2Response {
  co2e: number;
  co2e_unit: string;
  activity_data: {
    activity_value: number;
    activity_unit: string;
  };
  emission_factor: {
    name: string;
    source: string;
    year: number;
    region: string;
  };
}

const calculateElectricityCO2 = async (
  energy: number,
  energyUnit: string
): Promise<CO2Response | null> => {
  try {
    const response = await axios.post<CO2Response>(
      `https://api.climatiq.io/data/v1/estimate`,
      {
        emission_factor: {
          activity_id: 'electricity-supply_grid-source_residual_mix',
          data_version: '^6',
        },
        parameters: {
          energy: energy,
          energy_unit: energyUnit,
        },
      },
      {
        headers: {
          Authorization: `Bearer ${CLIMATIQ_API_KEY}`,
          'Content-Type': 'application/json',
        },
      }
    );
    return response.data;
  } catch (error: any) {
    console.error('Error calculating electricity CO2 emissions:', error);
    return null;
  }
};

const calculateCarCO2 = async (
  distance: number,
  vehicleType: string
): Promise<CO2Response | null> => {
  try {
    const response = await axios.post<CO2Response>(
      `https://api.climatiq.io/data/v1/estimate`,
      {
        emission_factor: {
          activity_id: 'passenger_vehicle-vehicle_type_automobile-fuel_source_na-distance_na-engine_size_na',
        },
        parameters: {
          distance: distance,
          distance_unit: 'km',
          vehicle_type: vehicleType,
        },
      },
      {
        headers: {
          Authorization: `Bearer ${CLIMATIQ_API_KEY}`,
          'Content-Type': 'application/json',
        },
      }
    );
    return response.data;
  } catch (error: any) {
    console.error('Error calculating car CO2 emissions:', error);
    return null;
  }
};

const CO2Calculator: React.FC = () => {
  const [energy, setEnergy] = useState('');
  const [energyUnit, setEnergyUnit] = useState('kWh');
  const [electricityResult, setElectricityResult] = useState<CO2Response | null>(null);
  const [electricityError, setElectricityError] = useState<string | null>(null);

  const [distance, setDistance] = useState('');
  const [vehicleType, setVehicleType] = useState('');
  const [carResult, setCarResult] = useState<CO2Response | null>(null);
  const [carError, setCarError] = useState<string | null>(null);

  const handleElectricityCalculate = async () => {
    if (!energy.trim()) {
      setElectricityError('Please enter the energy consumption.');
      return;
    }
    setElectricityError(null);
    const result = await calculateElectricityCO2(Number(energy), energyUnit);
    if (result) {
      setElectricityResult(result);
    } else {
      setElectricityError('An error occurred while calculating electricity CO2 emissions.');
    }
  };

  const handleCarCalculate = async () => {
    if (!distance.trim() || !vehicleType.trim()) {
      setCarError('Please enter both distance and vehicle type.');
      return;
    }
    setCarError(null);
    const result = await calculateCarCO2(Number(distance), vehicleType);
    if (result) {
      setCarResult(result);
    } else {
      setCarError('An error occurred while calculating car CO2 emissions.');
    }
  };

  return (
    <div className="flex flex-row h-full bg-gray-100 text-center p-4">
      {/* Left Panel: Electricity CO2 Calculator */}
      <div className="flex-1 flex flex-col items-center justify-center border-r border-gray-300 p-4">
        <h1 className="text-3xl font-bold text-blue-600 mb-4">Electricity CO2 Calculator</h1>
        <input
          type="text"
          value={energy}
          onChange={(e) => setEnergy(e.target.value)}
          placeholder="Enter energy consumption..."
          className="border border-gray-300 rounded p-2 mb-4"
        />
        <select
          value={energyUnit}
          onChange={(e) => setEnergyUnit(e.target.value)}
          className="border border-gray-300 rounded p-2 mb-4"
        >
          <option value="kWh">kWh</option>
          <option value="MWh">MWh</option>
        </select>
        <button
          onClick={handleElectricityCalculate}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Calculate
        </button>
        {electricityError && <p className="text-red-500 mt-2">{electricityError}</p>}
        {electricityResult && (
          <div className="mt-4 p-4 bg-white rounded shadow w-full max-w-md">
            <h2 className="text-2xl font-bold text-gray-800">CO2 Emissions</h2>
            <p className="text-lg text-gray-700">
              Energy Consumption: {electricityResult.activity_data.activity_value}{' '}
              {electricityResult.activity_data.activity_unit}
            </p>
            <p className="text-lg text-gray-700">
              Emission Factor: {electricityResult.emission_factor.name} (
              {electricityResult.emission_factor.region}, {electricityResult.emission_factor.year})
            </p>
            <p className="text-lg text-gray-700">
              CO2 Emissions: {electricityResult.co2e} {electricityResult.co2e_unit}
            </p>
          </div>
        )}
      </div>

      {/* Right Panel: Car CO2 Calculator */}
      <div className="flex-1 flex flex-col items-center justify-center p-4">
        <h1 className="text-3xl font-bold text-blue-600 mb-4">Car CO2 Calculator</h1>
        <input
          type="text"
          value={distance}
          onChange={(e) => setDistance(e.target.value)}
          placeholder="Enter distance in km..."
          className="border border-gray-300 rounded p-2 mb-4"
        />
        <select
          value={vehicleType}
          onChange={(e) => setVehicleType(e.target.value)}
          className="border border-gray-300 rounded p-2 mb-4"
        >
          <option value="">Select vehicle type...</option>
          <option value="small_petrol_car">Small Petrol Car</option>
          <option value="medium_petrol_car">Medium Petrol Car</option>
          <option value="large_petrol_car">Large Petrol Car</option>
          <option value="small_diesel_car">Small Diesel Car</option>
          <option value="medium_diesel_car">Medium Diesel Car</option>
          <option value="large_diesel_car">Large Diesel Car</option>
          <option value="electric_car">Electric Car</option>
          <option value="hybrid_car">Hybrid Car</option>
        </select>
        <button
          onClick={handleCarCalculate}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Calculate
        </button>
        {carError && <p className="text-red-500 mt-2">{carError}</p>}
        {carResult && (
          <div className="mt-4 p-4 bg-white rounded shadow w-full max-w-md">
            <h2 className="text-2xl font-bold text-gray-800">CO2 Emissions</h2>
            <p className="text-lg text-gray-700">Distance: {carResult.activity_data.activity_value} km</p>
            <p className="text-lg text-gray-700">Vehicle Type: {carResult.activity_data.activity_unit}</p>
            <p className="text-lg text-gray-700">
              CO2 Emissions: {carResult.co2e} {carResult.co2e_unit}
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default CO2Calculator;
