import React, { useState } from 'react';
import axios from 'axios';

const CO2_API_KEY = 'Bc0tU7kRVtzaHa6FQLcw'; // Replace with your Carbon Interface API key

export const calculateCO2 = async (distance, vehicleMake, vehicleModel, vehicleYear) => {
  try {
    const response = await axios.post(
      `https://www.carboninterface.com/api/v1/estimates`,
      {
        type: 'vehicle',
        distance_unit: 'km',
        distance_value: distance,
        vehicle_make: vehicleMake,
        vehicle_model: vehicleModel,
        vehicle_year: vehicleYear
      },
      {
        headers: {
          Authorization: `Bearer ${CO2_API_KEY}`,
          'Content-Type': 'application/json',
        },
      }
    );
    return response.data;
  } catch (error) {
    console.error('Error calculating CO2 emissions:', error.response?.data || error.message);
    return null;
  }
};

const CO2Calculator = () => {
  const [distance, setDistance] = useState('');
  const [vehicleMake, setVehicleMake] = useState('');
  const [vehicleModel, setVehicleModel] = useState('');
  const [vehicleYear, setVehicleYear] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleCalculate = async () => {
    if (!distance.trim() || !vehicleMake.trim() || !vehicleModel.trim() || !vehicleYear.trim()) {
      setError('Please enter all required fields.');
      return;
    }
    setError(null);
    const co2Result = await calculateCO2(Number(distance), vehicleMake, vehicleModel, vehicleYear);
    if (co2Result) {
      setResult(co2Result);
    } else {
      setError('An error occurred while calculating CO2 emissions.');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-full bg-gray-100 text-center p-4">
      <h1 className="text-3xl font-bold text-blue-600 mb-4">CO2 Calculator</h1>
      <input
        type="text"
        value={distance}
        onChange={(e) => setDistance(e.target.value)}
        placeholder="Enter distance in km..."
        className="border border-gray-300 rounded p-2 mb-4"
      />
      <input
        type="text"
        value={vehicleMake}
        onChange={(e) => setVehicleMake(e.target.value)}
        placeholder="Enter vehicle make..."
        className="border border-gray-300 rounded p-2 mb-4"
      />
      <input
        type="text"
        value={vehicleModel}
        onChange={(e) => setVehicleModel(e.target.value)}
        placeholder="Enter vehicle model..."
        className="border border-gray-300 rounded p-2 mb-4"
      />
      <input
        type="text"
        value={vehicleYear}
        onChange={(e) => setVehicleYear(e.target.value)}
        placeholder="Enter vehicle year..."
        className="border border-gray-300 rounded p-2 mb-4"
      />
      <button
        onClick={handleCalculate}
        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
      >
        Calculate
      </button>
      {error && <p className="text-red-500 mt-2">{error}</p>}
      {result && (
        <div className="mt-4 p-4 bg-white rounded shadow w-full max-w-md">
          <h2 className="text-2xl font-bold text-gray-800">CO2 Emissions</h2>
          <p className="text-lg text-gray-700">Distance: {result.data.attributes.distance_value} km</p>
          <p className="text-lg text-gray-700">Vehicle: {result.data.attributes.vehicle_make} {result.data.attributes.vehicle_model} ({result.data.attributes.vehicle_year})</p>
          <p className="text-lg text-gray-700">CO2 Emissions: {result.data.attributes.carbon_kg} kg</p>
        </div>
      )}
    </div>
  );
};

export default CO2Calculator;
