import React from 'react';

const About = () => {
    return (
        <div className="container mx-auto p-4">
            <h1 className="text-2xl font-bold mb-4">About This Application</h1>
            <p className="mb-2">
                This application is built using React, Tailwind CSS, and React Router. It serves as a demonstration of how to create a responsive web application with a modern tech stack.
            </p>
            <p className="mb-2">
                The application features multiple components, state management using Context API, and interacts with various APIs to fetch data.
            </p>
            <p>
                Explore the different pages to learn more about the functionalities and features implemented in this app.
            </p>
        </div>
    );
};

export default About;
